import { identifierName } from '@angular/compiler';
import { Component} from '@angular/core';
import { TranslateService } from '@ngx-translate/core';
@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {
  constructor(public translate: TranslateService) {
    translate.addLangs(['en', 'fr']);
    translate.setDefaultLang('en');
    const browserLang = translate.getBrowserLang();
    translate.use(browserLang.match(/en|fr/) ? browserLang : 'en');
  }
  switchLanguage(language: string) {
    this.translate.use(language);
  }

  public changeLanguage() {
    let currentLang = this.getCurrentLanguage()
    if (currentLang == 'en') {
    this.translate.use("fr");
    } else {
    this.translate.use("en");
    }
  }


  seasons: any[] = [
    { 1: 'Season 2022', 2: 'Season 2020'},
  ]
  public getData(i: number): number {
    let test = 12;
    return test;
  }

  public getSeasonNb(): number {
    let list = 10;
    return list;

  }

  public getSeasonName(i: number): string {
    let seasons = ['Season_2022', 'Season_2020', 'Season_2019', 'Season_2018' ];
    return seasons[i];
  }

  public getCurrentLanguage() {
    return this.translate.currentLang;
  }
}
